let Node = function(data) {
    this.data = data;
    this.parent = null;
    this.children = [];
}

Node.prototype = {
    constructor: Node,
    setParent: function(node) {
        node.addChild(this);
    },
    addChild: function(node) {
        node.parent = this;
        if(!contain(this.children, node)){
            this.children.push(node);
        }
    },
    getParent: function(){
        return this.parent;
    },
    getData: function(){
        return this.data;
    }
}

var contain = (list, item) => {
    for (let i = 0; i < list.length; i++) {
        if (item === list[i]) return true;
    }
    return false;
}

export default Node;