var arraylike = (obj) => obj && typeof obj === "object" && typeof obj.length === "number";

var checkEventType = (event, checks) => {
    for (let i = 0; i < checks.length; i++) {
        let ctxs = checks[i].ctxs;
        let type = checks[i].type;
        if (checkItem(event, ctxs, type)) {
            return true;
        }
    }
    return false;
}

var checkItem = (event, ctxs, type) => {
    if (typeof type === "string") {
        if (type !== event.type) {
            return false;
        }
    } else if (arraylike(type)) {
        for (var j = 0; j < type.length; j++) {
            if (type[j] === event.type) {
                break;
            }
        }
        if (j === type.length) {
            return false;
        }
    } else {
        if (!type.test(event.type)) {
            return false;
        }
    }
    if (ctxs) {
        for (let j = 0; j < ctxs.length; j++) {
            if (ctxs[j] === event.ctx) {
                return true;
            }
        }
    } else { // default is all
        return true;
    }
}

export default {
    checkEventType
}