import chainStyle from "chainstyle";

/**
 * SETMRule
 */

var SETMRule = chainStyle({
    interest: {
        checkType: ["falsy | array", "string | array | regExp"]
    },
    should: {
        checkType: ["function"]
    },
    lookBackAt: {
        checkType: ["number"]
    },
    lookForwardAt: {
        checkType: ["number"]
    }
}, {}, {
    chainRegular: /^((interest\.)+lookBackAt\.should)|((interest\.)+lookForwardAt\.should)$/
});

/**
 * Data merge
 */
var DataMergeRule = function() {}

DataMergeRule.prototype = {
    constructor: DataMergeRule,
    merge: function(mergeHandler) {
        this.mergeHandler = mergeHandler;
    }
}

/**
 * TriggerEventRule
 *
 * match
 */

var TriggerEventRule = function() {}

var TriggerEventRule = chainStyle({
    match: {
        checkType: ["falsy | array", "string | array | regExp"]
    }
}, {
    chainRegular: /^match$/
});


export default {
    SETMRule,
    TriggerEventRule,
    DataMergeRule
}