let Event = function(ctx, type, data, time) {
    this.ctx = ctx;
    this.type = type;
    this.data = data;
    this.time = time || new Date;

    this.isStopProp = false;
}

Event.prototype = {
    constructor: Event,
    clone: function() {
        return new Event(this.ctx, this.type, this.data, this.time);
    },
    isPropagationStopped: function(){
        return this.isStopProp;
    },
    stopPropagation: function(){
        this.isStopProp = true;
    }
}

export default Event;